package com.tka.july8;

public class fizzbuzz {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int num=15;
		 if(num%5==0&&num%3==0) {
			 System.out.println("fizzbuzz");
		 }
		 else if(num%3==0) {
			 System.out.println("fizz");
		 }
			 else if(num%5==0) {
				 System.out.println("buzz");
			 }
			
		 }
	}

